import React, { Component } from "react";
import Form from "./Form";
import CardContainer from "./CardContainer";
import ShowDetails from "./ShowDetails";
import "./App.scss";

export default class App extends Component {

  state = {
    foxes: []
  }

  componentDidMount(){
    fetch("http://localhost:3000/foxes")
    .then(res => res.json())
    .then(foxObject => {
      this.setState({
        foxes: foxObject
      })
    })

  }

  render() {
    return (
      <React.Fragment>
        <Form />
        { this.state.foxes.map((fox => {
        return <CardContainer fox={fox} key={fox.id} />
      }))
      } 
        {<ShowDetails />}

      </React.Fragment>
    );
  }
}
