import React, { Component } from "react";
import Form from "./Form";
import CardContainer from "./CardContainer";
import ShowDetails from "./ShowDetails";
import "./App.scss";

export default class App extends Component {

  state = {
    foxes: [], 
    foxItem: {}
  }

  handleShowClick = (fox) => {
    console.log("hey", fox )
    this.setState({
      foxItem: {}
    })
  }

  componentDidMount(){
    fetch("http://localhost:3000/foxes")
    .then(res => res.json())
    .then(foxObject => {
      this.setState({
        foxes: foxObject
      })
    })

  }

  render() {
    return (
      <React.Fragment>
        <Form />
        <CardContainer foxes={this.state.foxes} handleShowClick={this.handleShowClick} />
        <ShowDetails />

      </React.Fragment>
    );
  }
}
